mapper.select:
select c.id id , p.name productName , o.name orgName , p.credit credit , p.interest interest , p.period period , c.create_time collectTime , c.member_id memberid , c.product_id productid from c_collect_info c inner join m_member_info m on m.id = c.member_id left join m_product_info p on p.id = c.product_id left join m_organization_info o on o.number = p.bank where c.deleted = 0 and c.member_id = #{collectBean.memberid}
and p.name like concat(#{collectBean.proName} , '%')
and o.name like concat(#{collectBean.orgName} , '%')
limit #{start} , #{pageSize}

select count(*) from c_collect_info c inner join m_member_info m on m.id = c.member_id left join m_product_info p on p.id = c.product_id left join m_organization_info o on o.number = p.bank where c.deleted = 0 and c.member_id = #{collectBean.memberid}
and p.name like concat(#{collectBean.proName} , '%')
and o.name like concat(#{collectBean.orgName} , '%')

select p.id id , p.name name , concat_ws('' , a.name , c.name , z.name ) area , concat_ws('-' , p.credit_from , p.credit_to) credit, t.name productTypeName , i.name orgName from m_product_info p left join m_product_type t on p.type = t.id and t.deleted = 0 and t.status = 1 left join m_area_province a on a.code = p.province_id left join m_area_city c on c.code = p.city_id left join m_area_zone z on z.code = p.area_id left join m_organization_info i on i.id = p.bank where p.deleted = 0 and p.status = 2
and (p.credit_from >=5 and 5 <= p.credit_to) or (p.credit_from >=20 and 20 <= p.credit_to)
and (p.credit_from >=21 and 21 <= p.credit_to) or (p.credit_from >=50 and 50 <= p.credit_to)
and (p.credit_from >=51 and 51 <= p.credit_to) or (p.credit_from >=100 and 100 <= p.credit_to)
and (p.credit_from >=101 and 101 <= p.credit_to) or (p.credit_from >=200 and 200 <= p.credit_to)
and p.credit_to>200
and 3 between p.period_from and p.period_to
and 6 between p.period_from and p.period_to
and 12 between p.period_from and p.period_to
and 24 between p.period_from and p.period_to
and 36 between p.period_from and p.period_to
and 48 between p.period_from and p.period_to
and t.id = #{psearch.productType}
and a.code = #{psearch.province}
and c.code = #{psearch.city}
and z.code = #{psearch.zone}
and i.id = #{psearch.bank}
and p.name like concat(#{psearch.productName} , '%')
limit #{start} , #{pageSize}
