mapper.select:
select count(*) ordinaryNum , sum(l.score) ordinaryScore 
from m_member_info i inner join c_member_loan l on i.id = l.presenter 
where i.invitation_code =  #{code}

select l.id id , m.contact_name presenteeName , m.contact_phone presenteeTel , m.register_time presenteeRegTime , l.score presenterScore , l.money presenterMoney from c_member_loan l left join m_member_info i on i.id = l.presenter left join m_member_info m on m.id = l.presentee where l.presenter = #{memberid}
order by m.register_time desc
order by m.register_time asc
limit #{start} , #{pageSize}

select l.id id , m.real_name presenteeName , m.tel_number presenteeTel , m.created_time presenteeRegTime , l.score presenterScore , l.money presenterMoney from c_credit_loan l left join m_member_info i on i.id = l.presenter left join c_manager_info m on m.id = l.presentee where l.presenter = #{memberid}
order by m.register_time desc
order by m.register_time asc
limit #{start} , #{pageSize}
